const chalk = require("chalk");

const babel = require("./babel");

module.exports = function(options = {}) {
  const beg = Date.now();
  console.log(chalk.cyan(`Building package for Node ${options.version}...`));
  babel(options);
  const time = Date.now() - beg;
  console.log(chalk.green(`Completed building package in ${time} ms`));
};
