const paths = require("../lib/paths");

const packageJsonTemplate = pkg => {
  const npgVersion = require(`${paths.base}/packageJson`).version;
  const packageJson = {
    name: pkg.name,
    version: "0.1.0",
    description: "Package created with NPG (Node Package Generator)",
    main: "dist/index.js",
    scripts: {
      build: "npg build",
      start: "npg start"
    },
    dependencies: {
      // npg: npgVersion
      npg: `file:${paths.base}`
    }
  };
  return JSON.stringify(packageJson, null, "  ") + "\n";
};

module.exports = packageJsonTemplate;
